frappe.ui.form.on("CTN Transaction Sales Invoice", {
  ctn: function(frm, cdt, cdn) {
    let row = frappe.get_doc(cdt, cdn);

    if (!row.ctn) {
      frm.fields_dict["custom_ctn_transaction"].grid.get_field("item").get_query = () => ({});
      return;
    }

    frappe.call({
      method: "pos_ar.pos_ar.doctype.ctn_box.ctn_box.get_ctn_items",
      args: {
        // pass in row.ctn if thatâ€™s actually the box name
        ctn_box_name: row.ctn
      },
      callback: function(r) {

        if (r.message) {

          let allowed_items = r.message;

          console.log(allowed_items)

          frm.fields_dict["custom_ctn_transaction"].grid.get_field("item").get_query = 
            function(doc, cdt2, cdn2) {
              return { filters: { name: ["in", allowed_items] } };
            };

          if (row.item && !allowed_items.includes(row.item)) {
            frappe.model.set_value(cdt, cdn, "item", null);
          }
        }
      }
    });
  }
});

function update_ctn_transaction(frm){
    frm.clear_table("custom_ctn_transaction");
    let see = frm.doc.items.map((row) => {
        return {
            item: row.item_code, // rename item_code -> item
            qty: row.qty
        };
    })
    frappe.call({
        method: "pos_ar.pos_ar.doctype.ctn_box.ctn_box.get_ctn_for_invoice",
        args: {
            // Transform each row to { item: <code>, qty: <number> }
            items: frm.doc.items.map((row) => {
                return {
                    item: row.item_name, // rename item_code -> item
                    qty: row.qty,
                    warehouse: row.warehouse  
                };
            }),
            // pass the invoice name
            warehouse: frm.doc.set_warehouse,
        },
        callback: function(r) {
            if (r.message) {
            
                r.message.forEach(d => {
                    let row = frm.add_child("custom_ctn_transaction");
                    row.ctn = d.ctn;
                    row.item = d.item;
                    row.qty = d.qty;
                });
                frm.refresh_field("custom_ctn_transaction");
            }
        }
    });

    frm.refresh_field("custom_ctn_transaction");
}



















frappe.ui.form.on("Sales Invoice", {
    set_warehouse : function(frm) {
        update_ctn_transaction(frm);
    },
});






frappe.ui.form.on("Sales Invoice Item", {
