frappe.ui.form.on('Purchase Order', {
    custom_start(frm) {
        fetch_sold_items_if_ready(frm);
    },

    custom_end(frm) {
        fetch_sold_items_if_ready(frm);
    },

    company(frm) {
        fetch_sold_items_if_ready(frm);
    }
});



function fetch_sold_items_if_ready(frm) {
    const start = frm.doc.custom_start;
    const end = frm.doc.custom_end;
    const company = frm.doc.company;

    if (start && end && company) {
        frappe.call({
            method: "pos_ar.api.get_item_sold",
            args: { start, end, company },
            callback: function(r) {
                if (r.message) {
                    // Clear current items table
                    frm.clear_table("items");
                    // Add returned sold items to items table
                    r.message.forEach(row => {
                        const item = frm.add_child("items");
                        item.item_code = row.item_name;
                        item.item_name = row.item_name;
                        item.qty = row.total_qty;
                    });

                    frm.refresh_field("items");
                }
            }
        });
    } else {
        console.log("Waiting for all fields to be filled (start, end, company)");
    }
}

frappe.ui.form.on('CA-FRD', {
	refresh(frm) {
		// your code here

	},

	generatebtn(frm){
	    frappe.msgprint(__('Generate button clicked!'));
	    //CA_FRD_generator
	    frappe.call({
            method: "pos_ar.api.CA_FRD_generator",
            args : {
                ref : frm.doc.ref  ,
                max_count : frm.doc.max
            },
            callback: function(r) {
                console.log("result : " , r)
                if (r.message) {
                    // Clear current items table
                    frm.clear_table("ctns");
                    // Add returned sold items to items table
                    r.message.needed_ctns.forEach(row => {
                        const item = frm.add_child("ctns");
                        item.ctn  = row;
                    });

                    frm.refresh_field("ctns");


                    const html = generateCTNHtml(r.message.needed_ctns, r.message.ctn_details);
                    frm.fields_dict["ctn_details"].$wrapper.html(html);
                }
            }
        });
	}
})


// Helper to build the HTML
function generateCTNHtml(ctns, ctn_details) {
    let html = "";

    ctns.forEach(ctn => {
        html += `<h4 style="margin-top:16px; color:#4a4a4a;">ðŸ“¦ CTN: ${ctn}</h4>`;
        html += `<table class="table table-bordered" style="width:100%; margin-bottom: 20px;">
                    <thead>
                        <tr>
                            <th style="width: 60%;">Item Code</th>
                            <th style="width: 40%;">Quantity</th>
                        </tr>
                    </thead>
                    <tbody>`;
        (ctn_details[ctn] || []).forEach(item => {
            html += `
                <tr>
                    <td>${item.item}</td>
                    <td>${item.qty}</td>
                </tr>`;
        });
        html += `</tbody></table>`;
    });

    return html || "<p>No CTNs found.</p>";
}
