script:
frappe.ui.form.on('POS Closing Entry', {

    refresh: function(frm) {

        // Predefined denominations
        const denominations = [5, 10, 20, 50, 100 , 200 , 500, 1000, 2000];



        // Ensure calculations after all refresh operations
        frappe.after_ajax(async () => {
            //frappe.msgprint("we are here1")
            await waitForDataLoading(frm);
            calculate_total_money(frm);
        })


        // Check if the table is empty
        if (!frm.doc.custom_money || frm.doc.custom_money.length === 0) {
            denominations.forEach(denomination => {
                frm.add_child('custom_money', {
                    money : `${denomination} DA`,
                    qty   : 0, // Default count is 0
                    total : 0  // Default count is 0
                });
            });

            // Refresh the field to reflect changes
            frm.refresh_field('custom_money');
        }

    }
});

function waitForDataLoading(frm) {
    return new Promise((resolve) => {
        let checkInterval = setInterval(() => {

            console.log("we are working..... ")
            // Check if the necessary data is loaded
            if (frm.doc.payment_reconciliation && frm.doc.payment_reconciliation.length > 0 && frm.doc.pos_transactions && frm.doc.pos_transactions.length > 0) {
                console.log("we are working..... " , frm.doc.payment_reconciliation.length , "data" , frm.doc.payment_reconciliation)
                clearInterval(checkInterval);
                resolve();
            }
        }, 200); // Check every 200ms
    });
}


frappe.ui.form.on('money',{
    qty: function(frm, cdt, cdn) {
        let row = frappe.get_doc(cdt, cdn);
        if (row.money) {
            let money = parseInt(row.money.split(' ')[0]); // Extract number from money
            row.total = (row.qty || 0) * money; // Calculate total
            frm.refresh_field('custom_money'); // Refresh the child table
            calculate_total_denominations(frm)
        }

        calculate_total_money(frm)
    }
})

function calculate_total_denominations(frm){

    let total = 0

    if (frm.doc.custom_money) {
        frm.doc.custom_money.forEach(row => {
            total += row.total;
        });
    }
    frm.set_value('custom_closing_amount', total);
    frm.refresh_field('custom_closing_amount') ;
}



frappe.ui.form.on('POS Invoice Reference',{
    pos_transactions_add : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    pos_transactions_remove : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    pos_transactions : function(frm){
        calculate_total_money(frm);
    }
})


frappe.ui.form.on('POS Closing Entry Detail',{
    payment_reconciliation_add : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    payment_reconciliation_remove : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    closing_amount : function(frm){
        calculate_total_money(frm)	
    }
})


frappe.ui.form.on('check_in_out',{
    custom_check_in_out_add : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    custom_check_in_out_remove : function(frm , cdt , cdn){
        calculate_total_money(frm)	
    },
    amount : function(frm){
        calculate_total_money(frm)	
    },
    check_type : function(frm){
        calculate_total_money(frm)	
    }
})



function calculate_total_money(frm) {
    //frappe.msgprint("we are here")
    console.log(" in cal with : " , frm.doc.payment_reconciliation )
    let total_money = 0;

    let total_payment = 0 ;
    let total_check_in_out = 0 ;
    let closing_amount     = 0 ;

    closing_amount = frm.doc.custom_closing_amount

    // Calculate total from Payment Table
    if (frm.doc.payment_reconciliation) {
        frm.doc.payment_reconciliation.forEach(row => {
            total_payment += row.expected_amount || 0;
        });
    }

    // Calculate total from Payment Table
    if (frm.doc.custom_check_in_out) {
        frm.doc.custom_check_in_out.forEach(row => {
            if(row.check_type == "In"){
                total_check_in_out += row.amount || 0;    
            }else{
                total_check_in_out -= row.amount || 0;
            }

        });
    }

    total_money    = total_payment  + total_check_in_out

    console.log("total_payment : " , total_payment ," || custom_total_money : " , total_money , " || custom_closing_amount : " , closing_amount , " || custom_difference :  " , closing_amount - total_money)


    // Update Total Money field
    frm.set_value('custom_total_money', total_money);
    frm.set_value('custom_difference', closing_amount - total_money);

    // Refresh the field to reflect changes
    frm.refresh_field('custom_total_money');
    frm.refresh_field('custom_difference');
}

frappe.ui.form.on("CTN-BOX", {
  refresh(frm) {
    // If the document is already saved (not new):
    if (!frm.is_new()) {
      frm.set_df_property("ref"       , "read_only", 1);
      frm.set_df_property("ctn_num"   , "read_only", 1);
      frm.set_df_property("items"     , "read_only", 1);
      frm.set_df_property("company"   , "read_only", 1);
      frm.set_df_property("warehouse" , "read_only", 1);
    } else {
      frm.set_df_property("ref", "read_only", 0);
    }

    // Fetch transactions if saved
    if (!frm.is_new()) {
      frappe.call({
        method: "pos_ar.pos_ar.doctype.ctn_box.ctn_box.get_transactions",
        args: {
          ctn_box_name: frm.doc.name
        },
        callback: function(r) {
          if (r.message) {
            let data = r.message;
            
            // Build a Bootstrap-styled table with border-radius, no top margin
            let html = `
              <div class="table-responsive"
                   style="border: 1px solid #dee2e6; 
                          border-radius: 6px; 
                          overflow: hidden; 
                          margin: 0; 
                          padding: 0;">
                <table class="table table-hover table-striped mb-0" style="margin: 0;">
                  <thead>
                    <tr>
                      <th>Invoice</th>
                      <th>Item</th>
                      <th>Qty</th>
                    </tr>
                  </thead>
                  <tbody>
            `;

            data.forEach(row => {
              html += `
                <tr>
                  <td>${row.sales_invoice}</td>
                  <td>${row.item || ""}</td>
                  <td>${row.qty}</td>
                </tr>
              `;
            });

            html += `
                  </tbody>
                </table>
              </div>
            `;

            // Inject into the HTML field
            frm.fields_dict["transactions_html"].$wrapper.html(html);
            
            
            // 2) Now compute the "current state" of each Item.
            //    doc.items holds the initial quantities,
            //    and that each transaction row is an "outbound" (subtract) quantity.
            
            let itemMap = {};
            
            // A) Start with the "init items" in the CTN
            
            if (frm.doc.items) {
              frm.doc.items.forEach(initRow => {
                if (initRow.item) {
                  itemMap[initRow.item] = (itemMap[initRow.item] || 0) + (initRow.qty || 0);
                }
              });
            }
            
            // B) Add the transaction quantities
            data.forEach(txn => {
              if (txn.item) {
                itemMap[txn.item] = (itemMap[txn.item] || 0) + (txn.qty || 0);
              }
            });
            
            
            // 3) Build a second table for the "CTN Status" HTML field
            let statusHtml = `
              <div class="table-responsive"
                   style="border: 1px solid #dee2e6;
                          border-radius: 6px;
                          overflow: hidden;
                          margin-top: 1rem;">
                <table class="table table-hover table-striped mb-0" style="margin: 0;">
                  <thead>
                    <tr>
                      <th>Item</th>
                      <th>Remaining Qty</th>
                    </tr>
                  </thead>
                  <tbody>
            `;
            
            
            Object.keys(itemMap).forEach(itemCode => {
              statusHtml += `
                <tr>
                  <td>${itemCode}</td>
                  <td>${itemMap[itemCode]}</td>
                </tr>
              `;
            });
            
            
             statusHtml += `
                  </tbody>
                </table>
              </div>
            `;

            // 4) Inject the status table into your "ctn_status_html" field
            frm.fields_dict["ctn_status_html"].$wrapper.html(statusHtml);
            
          }else{
              
            // If no transactions found, clear the fields (or show a message)
            frm.fields_dict["transactions_html"].$wrapper.html("");
            frm.fields_dict["ctn_status_html"].$wrapper.html("No Transactions Found.");
          }
        }
      });
    } else {
      // If it's a new doc, clear both HTML fields
      frm.fields_dict["transactions_html"].$wrapper.html("");
      frm.fields_dict["ctn_status_html"].$wrapper.html("");
    }
  }
});
