# Link a submitted Purchase Invoice back to "Buy The Sold"
# Strategy:
# 1) Try PI Item.purchase_order → get that PO.custom_main_command (BTS name)
# 2) save

# ---------- helpers ----------
def split_bts_token(token):
    if not token:
        return {"base": None, "suffix": None}
    s = str(token).strip()
    idx = s.rfind("--")  # last '--'
    if idx == -1:
        return {"base": s, "suffix": None}
    return {"base": s[:idx].strip(), "suffix": s[idx+2:].strip() or None}


def pick_first(items, keys):
    for row in (items or []):
        for k in keys:
            if row.get(k):
                return row.get(k)
    return None
    
    
def qty_by_item(items):
    """Aggregate PI quantities per item_code."""
    m = {}
    for it in (items or []):
        code = it.get("item_code")
        if not code:
            continue
        # Prefer qty (in stock UOM). If you track in base units, switch to stock_qty.
        q = float(it.get("qty") or 0)
        m[code] = m.get(code, 0.0) + q
    return m


# ---------- 1) Resolve BTS + raw token from the PI's PO ----------
po = pick_first(doc.items, ["purchase_order"])
if po:
    raw_token = frappe.db.get_value("Purchase Order", po, "custom_main_command")
    if raw_token:
        tok      = split_bts_token(raw_token)
        bts_name = tok["base"]
        suffix   = tok["suffix"]

        if bts_name and frappe.db.exists("Buy The Sold", bts_name):
            # ---------- 2) Clear the correct PI link ONLY if it points to this PI ----------
            pi_field = "alter_facture_achat" if suffix == "AlterCommand" else "facture_achat"
            frappe.db.set_value("Buy The Sold", bts_name, pi_field, "---")

            # ---------- 3) Build delta from THIS PI only, then subtract ----------
            pi_delta = qty_by_item(doc.items)  # item_code -> qty from the canceled PI
            bts = frappe.get_doc("Buy The Sold", bts_name)

            if suffix == "AlterCommand" :
                for row in (bts.items or []):
                    frappe.log_error(" 7 ******>")
                    sold        = float(row.get("sold"))
                    found       = float(row.get("found"))
                    alter       = float(row.get("alter"))

                    # subtract this PI’s qty; clamp at 0
                    new_alter   = max(sold - found , 0.0)
                    not_found   = 0.0

                    frappe.db.set_value(row.doctype, row.name, "found",     found)
                    frappe.db.set_value(row.doctype, row.name, "alter",     new_alter)
                    frappe.db.set_value(row.doctype, row.name, "not_found", not_found)

            else :
                for row in (bts.items or []):
                    frappe.db.set_value(row.doctype, row.name, "found",     0.0)
                    frappe.db.set_value(row.doctype, row.name, "alter",     0.0)
                    frappe.db.set_value(row.doctype, row.name, "not_found", 0.0)
