def split_bts_token(token):
    if not token:
        return {"base": None, "suffix": None}
    s = str(token).strip()
    idx = s.rfind("--")  # last '--'
    if idx == -1:
        return {"base": s, "suffix": None}
    return {"base": s[:idx].strip(), "suffix": s[idx+2:].strip() or None}

# ---- Safe, non-blocking linking ----
raw = doc.get("custom_main_command")
if raw:  # only proceed if present; otherwise silently do nothing
    tok = split_bts_token(raw)
    bts_name = tok["base"]
    suffix   = tok["suffix"]

    if bts_name and frappe.db.exists("Buy The Sold", bts_name):
        fieldname = "alter_command_achat" if suffix == "AlterCommand" else "command_achat"
        frappe.db.set_value("Buy The Sold", bts_name, fieldname, doc.name, update_modified=False)
